}
.lblInputModal {
    margin-left: 32px;
    line-height: 40px;
    color: #6d6d6d;
}





.switch {
    position: relative;
    display: inline-block;
    width: 156px;
    height: 50px;
  }

  .switch input { 
    opacity: 0;
    width: 0;
    height: 0;
  }

  .slider {
    position: absolute;
    cursor: pointer;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background-color: #F7F7F7;
    transition: .4s;
  }

  .slider:before {
    position: absolute;
    content: "";
    height: 42px;
    width: 42px;
    left: 4px;
    bottom: 4px;
    background-color: #00FFA3;
    transition: .4s;
  }

  input:checked + .slider:before  {
    background-color: #FF0000;
  }


  input:checked + .slider:before {
    transform: translateX(106px);
  }

  /* Rounded sliders */
  .slider.round {
    border-radius: 34px;
  }

  .slider.round:before {
    border-radius: 50%;
  }

.timerTypeBlock {
    width: 100%;
    margin-top: 20px;
}
  .timerTypeSection {
      display: inline;
      vertical-align: middle;
